import java.util.HashMap;

public class MainActivity extends AppCompatActivity implements NavigationView.OnNavigationItemSelectedListener, BaseSliderView.OnSliderClickListener, ViewPagerEx.OnPageChangeListener {

    ImageView image1;
    CoordinatorLayout coordinator;
    DrawerLayout drawerLayout;
    NavigationView navigationView;
    Toolbar mainToolbar;
    SliderLayout slider;
    HashMap<String, Integer> hashMap;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        coordinator = findViewById(R.id.coordinator);
        drawerLayout = findViewById(R.id.drawerLayout);
        navigationView = findViewById(R.id.navigationView);
        mainToolbar = findViewById(R.id.main_toolbar);
        setSupportActionBar(mainToolbar);

        ActionBarDrawerToggle toggle;
        toggle = new ActionBarDrawerToggle(this, drawerLayout, mainToolbar, R.string.open, R.string.close);
        drawerLayout.addDrawerListener(toggle);
        toggle.syncState();

        navigationView.setNavigationItemSelectedListener(this);

        slider = findViewById(R.id.slider);
        hashMap = new HashMap<>();

        /*hashMap.put("firstItem", "data:image/jpeg;base64,");
        hashMap.put("secondItem", "data:image/jpeg;base64,");
        hashMap.put("thirdItem", "data:image/jpeg;base64,");
        hashMap.put("forth", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQeOLTtl_ZhcWlmWMExJ3t4P5cBnElgi9VbGA&usqp=CAU");*/
        /*hashMap.put("forth", String.valueOf(R.drawable.lexusb));
        hashMap.put("forth", String.valueOf(R.drawable.lexuse));*/
        hashMap.put("forth", R.drawable.lexuse);
        hashMap.put("forth", R.drawable.lexusb);

        for (String name: hashMap.keySet()) {
            TextSliderView textSliderView = new TextSliderView(getApplicationContext());
            textSliderView.description(name)
                    .image(hashMap.get(name))
                    .setScaleType(BaseSliderView.ScaleType.Fit)
                    .setOnSliderClickListener(this);
            textSliderView.bundle(new Bundle());
            textSliderView.getBundle().putString("extra", name);
            slider.addSlider(textSliderView);
        }

        slider.setPresetTransformer(SliderLayout.Transformer.Accordion);
        slider.setPresetIndicator(SliderLayout.PresetIndicators.Center_Bottom);
        slider.setCustomAnimation(new DescriptionAnimation());
        slider.setDuration(3000);
        slider.addOnPageChangeListener(this);

        /*image1 = findViewById(R.id.image1);

        Picasso.get().load(R.drawable.lexusb).into(image1);*/
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.option_menu, menu);
        return super.onCreateOptionsMenu(menu);
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        int optionId = item.getItemId();
        switch (optionId) {
            case R.id.page2:
                startActivity(new Intent(this, MainActivity2.class));
                break;
        }
        return super.onOptionsItemSelected(item);
    }

    @Override
    public boolean onNavigationItemSelected(@NonNull MenuItem item) {
        drawerLayout.closeDrawer(GravityCompat.START);
        return true;
    }

    @Override
    public void onBackPressed() {
        if (drawerLayout.isDrawerOpen(GravityCompat.START)) {
            drawerLayout.closeDrawer(GravityCompat.START);
        } else {
            super.onBackPressed();
        }
    }

    @Override
    public void onSliderClick(BaseSliderView slider) {
        Toast.makeText(getApplicationContext(), slider.getBundle().get("extra") + "", Toast.LENGTH_SHORT).show();
    }

    @Override
    public void onPageScrolled(int position, float positionOffset, int positionOffsetPixels) {

    }

    @Override
    public void onPageSelected(int position) {

    }

    @Override
    public void onPageScrollStateChanged(int state) {

    }

    @Override
    protected void onStop() {
        slider.stopAutoCycle();
        super.onStop();
    }
}